import React,{Component} from 'react';
// import emailjs from 'emailjs-com';
import emailjs from '@emailjs/browser';

import { Link } from 'react-router-dom'
// import { useState } from 'react/cjs/react.production.min';
import { useState } from 'react';

const Result =() =>{
    return(
        <p>your message has been submitted successfully</p>
    );
};
class Contact extends Component{
    constructor(props){
        super(props)

        this.state = {
            firstname: '',
            lastname: '',
            option: 'canada',
            message: ''
        }
    }

    handleFirstnameChange = (event) => {
        this.setState({
            firstname: event.target.value
        })
    }

    handleLastnameChange = (event) => {
        this.setState({
            lastname: event.target.value
        })
    }

    handleOptionChange = (event) => {
        this.setState({
            option: event.target.value
        })
    }

    handleMessageChange = (event) => {
        this.setState({
            message: event.target.value
        })
    }

    // handleSubmit = (event) => {
    //     // alert(`${this.state.firstname} ${this.state.lastname} ${this.state.option} ${this.state.message}`)
    //     var firstname = document.getElementById('firstname').value;
    //     var lastname = document.getElementById('lastname').value;
    //     var option = document.getElementById('option').value;
    //     var message = document.getElementById('message').value;
    //     window.location.href = "mailto:menakar273@gmail.com?firstname="+firstname+"&lastname"+lastname+"&option"+option+"&message"+message;

    //     event.preventDefault();
    //     return false;
    // }

    
    // const form = useRef();
    render(){
        
        // function handleSubmit(e) {
        //     e.preventDefault();
    
        //     emailjs.sendForm('gmail', 'template_wfq91zg', e.target, 'user_2Yv9G7U59CqaZyM9FBdvf')
        //       .then((result) => {
        //           console.log(result.text);
        //       }, (error) => {
        //           console.log(error.text);
        //       });
        //       e.target.reset()
        
        // }
        const [result, showResult] = useState(false);
        //const [result, showResult] = useState(false);
        const sendEmail = (e) => {
            e.preventDefault();
        
            emailjs.sendForm('gmail', 'template_wfq91zg', e.target, 'user_2Yv9G7U59CqaZyM9FBdvf')
              .then((result) => {
                  console.log(result.text);
              }, (error) => {
                  console.log(error.text);
              });
              e.target.reset();
              showResult(true);
          };

        return(
            <React.Fragment>
              {/* <!-- slider section start here --> */}
              <section>
                    <img src="assets/images/contact-us.jpg" alt="aboutpage-sectionone" class="firstsnap"/>
                </section>

                <section>
                    <div class="sec-contact"> 
                        <h2>Get in touch</h2>
                        <p>You will find yourself working in a true partnership that results in an incredible experience, and an end product that is the best.</p>
                    </div>
                </section>

                <section>
                    <div class="sec-form">
                    <form onSubmit={sendEmail}>
                        <label for="fname">First Name</label>
                        <input type="text" name='firstname' />
                        
                        {/* <label for="lname">Last Name</label>      
                        <input type="text" name='lastname' id='lastname' value={this.state.lastname} onChange={this.handleLastnameChange} />
                    
                        <label for="country">Country</label>
                        <select name='option' id='option' value={this.state.option} onChange={this.handleOptionChange}>
                            <option value="australia">Australia</option>
                            <option  value="canada">Canada</option>
                            <option value="usa">USA</option>
                        </select>

                        <label for="Message">Message</label>
                        <textarea name='message' id='message'  value={this.state.message} onChange={this.handleMessageChange}></textarea> */}
                        <input type="submit" value="Submit" />
                        <div>
                            {result ? <Result/> : null}
                        </div>
                    </form>
                    </div>
                </section>

            </React.Fragment> 
        )
    }
}
export default Contact;
