import React,{Component} from 'react';
import { Link } from 'react-router-dom'

class Contact extends Component{
    constructor(props){
        super(props)

        this.state = {
            firstname: '',
            lastname: '',
            gender: '',
            option: 'canada',
            message: ''
        }
    }

    handleFirstnameChange = (event) => {
        this.setState({
            firstname: event.target.value
        })
    }

    handleLastnameChange = (event) => {
        this.setState({
            lastname: event.target.value
        })
    }

    handleGenderChange = (event) => {
        this.setState({
            gender: event.target.value
        })
    }

    handleOptionChange = (event) => {
        const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });

        // this.setState({
        //     option: event.target.value
        // })
    }

    handleMessageChange = (event) => {
        this.setState({
            message: event.target.value
        })
    }

    handleSubmit = (event) => {
        alert(`${this.state.firstname} ${this.state.lastname} ${this.state.value} ${this.state.option} ${this.state.message}`)
        event.preventDefault()
    }

    render(){
        return(
            <React.Fragment>
              {/* <!-- slider section start here --> */}
              <section>
                    <img src="assets/images/contact-us.jpg" alt="aboutpage-sectionone" class="firstsnap"/>
                </section>

                <section>
                    <div class="sec-contact"> 
                        <h2>Get in touch</h2>
                        <p>You will find yourself working in a true partnership that results in an incredible experience, and an end product that is the best.</p>
                    </div>
                </section>

                <section>
                    <div class="sec-form">
                    <form onSubmit={this.handleSubmit}>
    <label for="fname">First Name</label>
    <input type="text" value={this.state.firstname} onChange={this.handleFirstnameChange} />

    <label for="lname">Last Name</label>
    <input type="text" value={this.state.lastname} onChange={this.handleLastnameChange} />

    <label for="gender">Male</label>
    <input type="checkbox" value={this.state.gender} onChange={this.handleGenderChange} />

    <label for="country">Country</label>
    <select value={this.state.option} onChange={this.handleOptionChange}>
      <option value="australia">Australia</option>
      <option  value="canada">Canada</option>
      <option value="usa">USA</option>
    </select>
    <label for="Message">Message</label>
    <textarea value={this.state.message} onChange={this.handleMessageChange} rows="9" cols="50"></textarea>
    {/* <input type="textarea" id="message" name="message" placeholder="Message here..." /> */}
    <input type="submit" value="Submit" />
  </form>
                    </div>
                </section>

            </React.Fragment> 
        )
    }
}
export default Contact;
